import React, { Component } from "react";

class AddPlayerForm extends Component {

    state = {
        value: 'Neil'
    }

    handleValueChange = (e) => {
        console.log(e);
    }

    render() {
        return (
            <form>
                <input 
                    type="text"
                    value= {this.state.value}
                    onChange = {this.handleValueChange()}
                    placeholder="Enter player name"
                />

                <input 
                    type="submit"
                    value="Add player"
                />
            </form>
        );
    }
}

export default AddPlayerForm
